-- Create daily_leaderboard table
CREATE TABLE daily_leaderboard (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  day text NOT NULL,            -- YYYYMMDD
  name text NOT NULL,           -- player display name
  score int NOT NULL,           -- daily points (e.g., 100)
  created_at timestamptz DEFAULT now()
);

-- Create index on (day, score desc) for efficient leaderboard queries
CREATE INDEX idx_daily_leaderboard_day_score ON daily_leaderboard (day, score DESC);

-- Create unique constraint on (day, name) to prevent duplicate submissions per day per name
CREATE UNIQUE INDEX idx_daily_leaderboard_day_name_unique ON daily_leaderboard (day, name);

-- Enable RLS
ALTER TABLE daily_leaderboard ENABLE ROW LEVEL SECURITY;

-- Create RLS policies
-- Allow anyone to read leaderboard data (public leaderboard)
CREATE POLICY "Anyone can view leaderboard" ON daily_leaderboard FOR SELECT USING (true);

-- Allow anyone to insert their score (no auth required for this simple game)
CREATE POLICY "Anyone can submit scores" ON daily_leaderboard FOR INSERT WITH CHECK (true);

-- Allow users to update their own score for the same day (upsert functionality)
CREATE POLICY "Users can update their own daily score" ON daily_leaderboard FOR UPDATE USING (true);

-- Create events table for analytics tracking
create table events (
  id uuid primary key default gen_random_uuid(),
  ts timestamptz default now(),
  type text not null,
  props jsonb default '{}'::jsonb,
  uid text,
  app_ver text
);

-- Create indexes for performance
create index events_ts_desc_idx on events (ts desc);
create index events_type_idx on events (type);

-- Enable RLS
alter table events enable row level security;

-- Create RLS policies (allow all for now since this is analytics data)
create policy "Allow all access to events" on events for all using (true);

-- 1. Create feedback table
create table feedback (
  id uuid primary key default gen_random_uuid(),
  ts timestamptz default now(),
  name text,
  category text not null,
  message text not null,
  stage int,
  level int,
  points int,
  device text,
  app_ver text
);

-- 2. Create index on timestamp
create index idx_feedback_ts on feedback (ts desc);

-- 3. Enable RLS
alter table feedback enable row level security;

-- 4. Create RLS policies (allow all users to insert feedback, admins to view)
create policy "Anyone can submit feedback" on feedback for insert with check (true);
create policy "Admins can view all feedback" on feedback for select using (
  exists (
    select 1 from app_admins 
    where user_id = auth.uid()
  )
);

-- Create word_sets table
CREATE TABLE word_sets (
    id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
    theme text UNIQUE NOT NULL,
    words text[] NOT NULL,
    version int DEFAULT 1,
    updated_at timestamptz DEFAULT now()
);

-- Enable RLS
ALTER TABLE word_sets ENABLE ROW LEVEL SECURITY;

-- Create RLS policies (allow all operations for now - can be restricted later)
CREATE POLICY "Allow all access to word_sets" ON word_sets FOR ALL USING (true);

-- Create index for performance
CREATE INDEX word_sets_updated_at_idx ON word_sets (updated_at DESC);
CREATE INDEX word_sets_theme_idx ON word_sets (theme);

-- Create seasonal_challenges table
CREATE TABLE seasonal_challenges (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  name text NOT NULL,
  words text[] NOT NULL,
  active_from date,
  active_to date,
  version int DEFAULT 1,
  updated_at timestamptz DEFAULT now()
);

-- Create indexes for performance
CREATE INDEX idx_seasonal_challenges_dates ON seasonal_challenges (active_from, active_to);
CREATE INDEX idx_seasonal_challenges_updated_at ON seasonal_challenges (updated_at DESC);

-- Enable RLS
ALTER TABLE seasonal_challenges ENABLE ROW LEVEL SECURITY;

-- Create RLS policies (read-only for all users since these are public challenges)
CREATE POLICY "Everyone can view active challenges" ON seasonal_challenges 
  FOR SELECT USING (true);

-- Only allow inserts/updates from authenticated users (for admin functionality)
CREATE POLICY "Authenticated users can manage challenges" ON seasonal_challenges 
  FOR ALL USING (auth.role() = 'authenticated');

SELECT name, active_from, active_to FROM seasonal_challenges 
WHERE active_from <= CURRENT_DATE AND active_to >= CURRENT_DATE 
ORDER BY updated_at DESC;

-- Add columns to support Challenges in word_sets table
ALTER TABLE word_sets 
ADD COLUMN kind text NOT NULL DEFAULT 'Stage',
ADD COLUMN active_from date,
ADD COLUMN active_to date;

-- Add check constraint for kind values
ALTER TABLE word_sets 
ADD CONSTRAINT word_sets_kind_check CHECK (kind IN ('Stage', 'Challenge'));

-- Create indexes for efficient querying
CREATE INDEX idx_word_sets_kind ON word_sets (kind);
CREATE INDEX idx_word_sets_active_dates ON word_sets (active_from, active_to);

