import React from "react";
import { Pressable, Text, StyleSheet, View, ViewStyle } from "react-native";
import { triggerHaptic, Haptic } from "../services/HapticsService";

let LinearGradient: any = View;
try { LinearGradient = require("expo-linear-gradient").LinearGradient; } catch {}

type Props = {
  label?: string; icon?: string; onPress?: ()=>void;
  onLongPress?: ()=>void; disabled?: boolean;
  haptic?: Haptic; width?: number|"auto"|"full";
  style?: ViewStyle; testID?: string;
};

export default function GradientButton({
  label, icon, onPress, onLongPress, disabled,
  haptic="light", width="full", style, testID
}: Props) {
  const wStyle = width==="full"?{alignSelf:"stretch"}:width==="auto"?{}:{width};
  return (
    <Pressable
      accessibilityRole="button"
      disabled={disabled}
      testID={testID}
      onPressIn={()=>triggerHaptic(haptic)}
      onPress={onPress} onLongPress={onLongPress}
      style={({pressed})=>[
        styles.wrap,wStyle,style,
        pressed&&!disabled?styles.pressed:null,
        disabled?{opacity:0.5}:null
      ]}
    >
      <LinearGradient colors={["#6a5acd","#00c6ff"]}
        start={{x:0,y:0}} end={{x:1,y:1}}
        style={styles.grad}>
        {!!icon&&<Text style={styles.icon}>{icon}</Text>}
        {!!label&&<Text style={styles.label}>{label}</Text>}
      </LinearGradient>
    </Pressable>
  );
}

const styles=StyleSheet.create({
  wrap:{borderRadius:999,overflow:"hidden",marginVertical:6},
  grad:{paddingVertical:14,paddingHorizontal:18,borderRadius:999,
    alignItems:"center",justifyContent:"center",flexDirection:"row",
    gap:8,minHeight:48},
  label:{fontSize:16,fontWeight:"700",color:"#fff"},
  icon:{fontSize:18,color:"#fff",marginRight:6},
  pressed:{transform:[{scale:0.98}]}
});
